sql.where:
ro.is_delete = '0'

  AND ro.bg_room_status = #{bgRoomStatus,jdbcType=VARCHAR}


  AND ro.bg_room_merchant_id = #{bgRoomMerchantId,jdbcType=VARCHAR}


  AND ro.bg_room_number = #{bgRoomNumber,jdbcType=VARCHAR}


  AND ro.create_time  >=  #{startDate,jdbcType=DATE}


  AND ro.create_time  <=  #{endDate,jdbcType=DATE}

mapper.update:
update bg_room
set is_delete = #{isDelete, jdbcType=VARCHAR},
update_by = #{updateBy, jdbcType=VARCHAR},
update_time = #{updateTime, jdbcType=VARCHAR}

  is_delete = '0'
  AND bg_room_id = #{bgRoomId,jdbcType=VARCHAR}

mapper.select:
select
from bg_room ro left JOIN bg_room_info roi on ro.bg_room_id = roi.bg_room_info_id left JOIN bg_data_dict_value on ro. val
ORDER BY create_time,bg_merchant_id LIMIT #{page.startRow,jdbcType=INTEGER},#{page.pageSize,jdbcType=INTEGER}

mapper.update:
update bg_room
set bg_room_status = #{bgRoomStatus,jdbcType=CHAR},
update_by = #{updateBy,jdbcType=VARCHAR},
update_time = #{updateTime,jdbcType=TIMESTAMP}

  bg_room_id = #{bgRoomId,jdbcType=VARCHAR}
  AND is_delete = '0'
  AND bg_room_status != #{bgRoomStatus,jdbcType=CHAR}
