sql.where:
is_delete = '0'

  AND bg_user_name LIKE  '%' || #{bgUserName,jdbcType=VARCHAR} || '%'


  AND bg_user_phone LIKE  '%' || #{bgUserPhone,jdbcType=VARCHAR} || '%'


  AND bg_user_emp_id LIKE  '%' || #{bgUserEmpId,jdbcType=VARCHAR} || '%'


  AND create_time  >=  #{startDate,jdbcType=DATE}


  AND create_time  <=  #{endDate,jdbcType=DATE}

mapper.select:
select
from bg_user
ORDER BY create_time,bg_user_id LIMIT #{page.startRow,jdbcType=INTEGER},#{page.pageSize,jdbcType=INTEGER}

mapper.update:
update bg_user
set is_delete = #{isDelete, jdbcType=VARCHAR},
update_by = #{updateBy, jdbcType=VARCHAR},
update_time = #{updateTime, jdbcType=VARCHAR}

  is_delete = '0'
  AND bg_user_id = #{bgUserId,jdbcType=VARCHAR}

update bg_user
set is_active = #{isActive,jdbcType=CHAR},
update_by = #{updateBy,jdbcType=VARCHAR},
update_time = #{updateTime,jdbcType=TIMESTAMP}

  bg_user_id = #{bgUserId,jdbcType=VARCHAR}
  AND is_delete = '0'
  AND is_active != #{isActive,jdbcType=CHAR}
