sql.where:
is_delete = '0'

  AND bg_role_name LIKE  '%' || #{bgRoleName,jdbcType=VARCHAR} || '%'


  create_time  >=  #{startDate,jdbcType=DATE}


  create_time  <=  #{endDate,jdbcType=DATE}

mapper.select:
select
from bg_role
ORDER BY create_time,bg_role_id LIMIT #{page.startRow,jdbcType=INTEGER},#{page.pageSize,jdbcType=INTEGER}

mapper.update:
update bg_role
set is_delete = #{isDelete, jdbcType=VARCHAR},
    update_by = #{updateBy, jdbcType=VARCHAR},
    update_time = #{updateTime, jdbcType=VARCHAR}

  is_delete = '0'
  AND bg_role_id = #{bgRoleId,jdbcType=VARCHAR}

update bg_role
set is_active = #{isActive,jdbcType=CHAR},
  update_by = #{updateBy,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=TIMESTAMP}

    bg_role_id = #{bgRoleId,jdbcType=VARCHAR}
    AND is_delete = '0'
    AND is_active != #{isActive,jdbcType=CHAR}
